let tags=document.querySelectorAll(".tag"),images={charcoal:9,coir:26,granite:5};function loadImage(e,t){let a=[],r=document.querySelector(".img-container");for(let i=1;i<=t;i++)a.push(`<div class="img"><img src="/public/images/gallery/${e}/${i}.jpeg"></div>`);r.innerHTML=a.join("")}loadImage("charcoal",9),tags.forEach(e=>{e.addEventListener("click",e=>{document.querySelector("#active").setAttribute("id",""),e.target.setAttribute("id","active"),loadImage(e.target.innerHTML.toLowerCase(),images[e.target.innerHTML.toLowerCase()])})});
